module.exports.fn = function(arr) {
    var newArr = [];
    arr.forEach(function(v, i) {
        if (newArr.indexOf(v) == -1) {
            newArr.push(v)
        }
    });
    return newArr.sort(function(a, b) {
        return a - b;
    });

    // return arr.filter(function(v, i) {
    //     console.log(i, v)
    //     return arr.indexOf(v) === i
    // });
}