module.exports.newArray = {
    unique: function(arr) {
        var newArr = [];
        for (var i = 0; i < arr.length; i++) {
            if (newArr.indexOf(arr[i]) == -1) {
                newArr.push(arr[i])
            }
        }
        return newArr;
    },
    sortArr: function(arr) {
        arr.sort(function(a, b) {
            return a - b;
        });
        return arr;
    }
}